   FOR EACH ROW BEGIN
         INSERT INTO trigger_test VALUES('added new employee');
   END$$
DELIMITER ;

DELIMITTER $$
CREATE TRIGGER my_trigger BEFORE INSERT ON employee
   FOR EACH ROW BEGIN
         INSERT INTO trigger_test VALUES(NEW.first_name);
   END$$
DELIMITER ;

DELIMITTER $$
CREATE TRIGGER my_trigger BEFORE INSERT ON employee
   FOR EACH ROW BEGIN
         IF NEW.sex = 'F' THEN          
         INSERT INTO trigger_test VALUES('ADD female");
         ELSE IF NEW.sex='M' THEN
           INSERT INTO trigger_test VALUES('ADD male');
         ELSE
           INSERT INTO trigger_test VALUES('No gender');
         ENDIF;
   END$$
DELIMITER ;

